/* SortableJS - Minimal Fallback Implementation */
/* Fallback for when CDN is unavailable */

(function(window) {
    'use strict';

    if (typeof window.Sortable === 'undefined') {
        window.Sortable = function(element, options) {
            console.warn('SortableJS not loaded. Drag-and-drop functionality disabled.');
            return {
                destroy: function() {},
                option: function() {},
                closest: function() { return null; },
                toArray: function() { return []; },
                sort: function() {},
                save: function() {},
                load: function() {},
                destroy: function() {}
            };
        };

        window.Sortable.create = function(element, options) {
            return new window.Sortable(element, options);
        };

        window.Sortable.version = '1.15.0-local-fallback';
    }
})(window);
